package javaprog;

public class classOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 5;
		int m = 5;
		int o = 5;
		int p = 5;
		
		System.out.println(n++);
		System.out.println(++m);
		System.out.println(o--);
		System.out.println(--p);

	}

}
